<?php/** * 修正已经绑定用户的UID *  * @package scripts * @author fengjun <fengj@peptalk.cn> */ini_set('display_errors', 1);ini_set('error_reporting', 'E_ALL & ~E_NOTICE');$args = $_SERVER['argv'];unset($args[0]);define('BETTER_FORCE_INCLUDE', true);define('BETTER_START_TIME', microtime());//	进程锁define('SYNC_BLOG_LOCK', dirname(__FILE__).'/sync_avatar.lock');define('THIS_PID', dirname(__FILE__).'/sync_avatar.pid');//	标定环境为Crondefine('IN_CRON', true); // 定义Better路径defined('APPLICATION_PATH') || define('APPLICATION_PATH', realpath(dirname(__FILE__) . '/../application'));defined('APPLICATION_ENV') || define('APPLICATION_ENV', (getenv('APPLICATION_ENV') ? getenv('APPLICATION_ENV') : 'production'));function killLock() { 	file_exists(SYNC_BLOG_LOCK) && unlink(SYNC_BLOG_LOCK); }set_time_limit(0);//error_reporting(0);date_default_timezone_set('UTC');// 检测是否有同步锁file_exists(SYNC_BLOG_LOCK) && exit(0);//	没有同步锁则继续执行同步操作register_shutdown_function('killLock');// Ensure library/ is on include_pathset_include_path(implode(PATH_SEPARATOR, array(    realpath(APPLICATION_PATH . '/../library'),    get_include_path(),)));require_once 'Better/Cache.php';require_once 'Better/Cache/Handler/Base.php';require_once 'Better/Loader.php';Better_Loader::getInstance()->register();Better_Config::load();$appConfig = Better_Config::getAppConfig();//	定义常量，减少array_key_exists调用define('BETTER_NOW', time());define('BETTER_BASE_URL', Better_Config::getAppConfig()->base_url);define('BETTER_DB_TBL_PREFIX', Better_Config::getDbConfig()->global->tbl_prefix);define('BETTER_SYS_UID', $appConfig->user->sys_user_id);define('BETTER_VIRTUAL_UID', $appConfig->user->virtual_user_id);define('BETTER_DB_DEBUG', Better_Config::getDbConfig()->global->debug==1 ? true : false);define('BETTER_ENABLE_LOG', $appConfig->log->enable==1 ? true : false);define('BETTER_QBS_DEBUG', $appConfig->qbs->debug==1 ? true : false);define('BETTER_LOG_HASH', md5(uniqid(rand())));define('BETTER_PAGE_SIZE', $appConfig->blog->page_size);define('BETTER_MAX_LIST_ITEMS', $appConfig->blog->list_max_items);define('BETTER_CACHE_HANDLER', $appConfig->cache->handler);define('PHP_EXE', Better_Config::getAppConfig()->php_exe);$sess = Better_Session::factory();$sess->init();$protocols = array(			'sina.com',//			'kaixin001.com',//			'sohu.com',//			'qq.com'			);$rows = Better_DAO_ThirdBinding::getAllBindUser('sina.com');var_dump(count($rows));$i = $c = $f = 0;foreach ($rows as $row) {	echo "\r\n";	try {		$uid = $row['uid'];		$protocol = $row['protocol'];		$avatar = $row['avatar'];		$nickname = $row['nickname'];		$username = $row['username'];		$password = $row['password'];			$oauth_token = $row['oauth_token'];			$oauth_token_secret = $row['oauth_token_secret'];				if (!$oauth_token || !$oauth_token_secret) continue;				$_user = Better_User::getInstance($uid);		if ( $avatar ) { //有头像啦 还不可以跳过 还的判断一下是不是缺省头像啊						$userinfo = $_user->getUserInfo();			$avatar_url = $userinfo['avatar_url']; // http://k.ai/files/avatar/06/14/15/448765.263729.jpeg						$filename = str_replace('http://k.ai', '/usr/local/apache2', $avatar_url);			$size = filesize($filename);						if ($size != 1528) {				$c++;				var_dump('c' . $c);				continue;							} else {				var_dump("$uid, 1528", $avatar_url);			}		} 				$service = Better_Service_PushToOtherSites::factory($protocol, $username, $password, $oauth_token, $oauth_token_secret);				var_dump($protocol, $username, $password, $oauth_token, $oauth_token_secret);		if ($service->verify_credentials()) {			$third_info = $service->getInfo();						// 头像没有啦 这怎么行啊			$url = $service->getAvatar();			$result = $_user->avatar()->upload($url, 'jpg', $uid);			var_dump('update avatar' . $url);						$i++;			var_dump('i' . $i);				} else {			$f++;			var_dump('f' . $f);		}	} catch (Exception $ee) {		var_dump($ee->getTraceAsString());		continue;	}}var_dump($c, $i, $f);